<script type="9aa284d1f4fba027fb076280-text/javascript">
    var initApplication = function() {
        var doctorLink = document.querySelectorAll('.doctor_box_link');

        doctorLink.forEach(el => el.addEventListener('click', event => {
            event.preventDefault();
        }));

        setTimeout(function() {
            var buttonizer = document.querySelectorAll('.buttonizer-button');

            buttonizer.forEach((el, idx) => {
                setTimeout(function() {
                    el.classList.add("vibrate-animate")
                }, idx * 100)
            });
        }, 3000);


        // read the link on how above code works

        var findMe = document.querySelectorAll('.slide-right');

        console.log("findMe", findMe)

        document.addEventListener('scroll', function(event) {
            // add event on scroll
            findMe.forEach(element => {
                console.log("element", element);
                //for each .thisisatest
                if (isInViewport(element)) {
                    //if in Viewport
                    element.classList.add("draw");
                } else {
                    element.classList.remove("draw");
                }
            });
        }, false);
    };

    document.onreadystatechange = function() {
        if (document.readyState == "complete") {
            initApplication();
        }
    }
</script>
